import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceDetection from './components/FaceDetection/FaceDetection';
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import './App.css';
import ParticlesBg from 'particles-bg';

// const returnClarifaiRequest = (imageUrl) => {

//    const PAT = '15fe0f2c689344c4b999e5c475ebac46';
//    const USER_ID = 'clarifaiapiel21';       
//    const APP_ID = 'face-detection';
//    const IMAGE_URL = imageUrl;

//    const raw = JSON.stringify({
//       "user_app_id": {
//          "user_id": USER_ID,
//          "app_id": APP_ID
//       },
//       "inputs": [
//          {
//                "data": {
//                   "image": {
//                      "url": IMAGE_URL
//                   }
//                }
//          }
//       ]
//    });

//    const requestOptions = {
//       method: 'POST',
//       headers: {
//          'Accept': 'application/json',
//          'Authorization': 'Key ' + PAT
//       },
//       body: raw
//    };

//    return requestOptions;
// }

const initialState = {
   input: '',
   imageUrl: '',
   boxes: [],
   route: 'signin',
   isSignedIn: false,
   user: {
      id: '',
      name: '',
      email: '',
      entries: 0,
      joined: ''
   }
}

class App extends Component {

   constructor(){
      super();
      this.state = initialState;
   }
   // componentDidMount() {
   //    fetch('http://localhost:3000/')
   //       .then(response => response.json())
   //       .then(data => console.log(data))
   // }
   // we will create smart component in Signin, so no need to create it in App.js

   loadUser = (data) => {
      this.setState({user: {
         id: data.id,
         name: data.name,
         email: data.email,
         entries: data.entries,
         joined: data.joined
      }})
   }

   calculateFaceLocation = (clarifaiOutput) => {
      const image = document.getElementById('inputimage');
      const width = Number(image.width);
      const height = Number(image.height);
      const faces = clarifaiOutput.outputs[0].data.regions;
      return faces.map(face => {
         const clarifaiFace = face.region_info.bounding_box;
         return {
            leftCol: clarifaiFace.left_col * width,
            topRow: clarifaiFace.top_row * height,
            rightCol: width - (clarifaiFace.right_col * width),
            bottomRow: height - (clarifaiFace.bottom_row * height)
         }
      })
   }

   displayFaceBox = (boxes) => {
      this.setState({boxes: boxes});
   }

   onInputChange = (event) => {
      this.setState({input: event.target.value});
   }

   onBtnSubmit = () => {
      this.setState({imageUrl: this.state.input});
      // fetch(`https://api.clarifai.com/v2/models/face-detection/outputs`, returnClarifaiRequest(this.state.input))
         fetch('http://localhost:3000/imageurl', {
            method: 'post',
            headers: {'Content-type': 'application/json'},
            body: JSON.stringify({
               input: this.state.input
            })
         })
         .then(response => response.json())
         .then(response => {
            if(response) {
               fetch('http://localhost:3000/image', {
                  method: 'put',
                  headers: {'Content-type': 'application/json'},
                  body: JSON.stringify({
                     id: this.state.user.id
                  })
               })
                  .then(entries => entries.json())
                  .then(count => {
                     this.setState(Object.assign(this.state.user, {
                        entries: count}))
                  })
                  .catch(console.log)
               }
            this.displayFaceBox(this.calculateFaceLocation(response))
         })
         .catch(err => console.log(err))
   }

   onRouteChange = (route) => {
      if (route === 'signout'){
         this.setState(initialState)
      } else if (route === 'home'){
         this.setState({isSignedIn: true})
      }
      this.setState({route: route})
   }

   render() {
      const { isSignedIn, imageUrl, route, boxes, user } = this.state;
      return (
         <div className="App">
            <ParticlesBg color="#FFEE00" num={150} type="cobweb" bg={true} />
            <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange} />
            { route === 'home'
               ? <div>
                     <Logo />
                     <ImageLinkForm onInputChange={this.onInputChange} onBtnSubmit={this.onBtnSubmit} />
                     <Rank name={user.name} entries={user.entries}/>
                     <FaceDetection boxes={boxes} imageUrl={imageUrl} />
                 </div>
               : (
                  route === 'signin' 
                  ? <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
                  : <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
                 )
            }
         </div>
      )
   }
}

export default App;
